import React, { useState } from "react";
import "./App.css";

const App = () => {
  const choices = ["Rock", "Pape", "Scissors"];
  const [playerChoice, setPlayerChoice] = useState("");
  const [computerChoice, setComputerChoice] = useState("");
  const [result, setResult] = useState("");
  const [playerScore, setPlayerScore] = useState(0);
  const [computerScore, setComputerScore] = useState(0);
  const [gameHistory, setGameHistory] = useState([]);

  const determineWinner = (player, computer) => {
    if (player === computer) return "It's a tie!";
    if (
      (player === "Rock" && computer === "Scissors") ||
      (player === "Scissors" && computer === "Paper") ||
      (player === "Paper" && computer === "Rock")
    ) {
      setPlayerScore(playerScore + 1);
      return "You Win!";
    }
    setComputerScore(computerScore + 1);
    return "Computer Wins!";
  };

  const handleChoiceClick = (choice) => {
    const computerRandomChoice = choices[Math.floor(Math.random() * choices.length)];
    setPlayerChoice(choice);
    setComputerChoice(computerRandomChoice);
    const gameResult = determineWinner(choice, computerRandomChoice);
    setResult(gameResult);

    setGameHistory([
      ...gameHistory,
      { round: gameHistory.length + 1, player: choice, computer: computerRandomChoice, result: gameResult },
    ]);
  };

  return (
    <div className="app">
      <h1>Rock, Paper, Scissors Game</h1>

      <div className="scoreboard">
        <p>Player Score: {playerScore}</p>
        <p>Computer Score: {computerScore}</p>
      </div>

      <div className="choices">
        {choices.map((choice) => (
          <button key={choice} onClick={() => handleChoiceClick(choice)}>
            {choice}
          </button>
        ))}
      </div>

      <div className="results">
        <p>Player Chose: {playerChoice}</p>
        <p>Computer Chose: {computerChoice}</p>
        <h2>{result}</h2>
      </div>

      <h2>Game History</h2>
      <ul>
        {gameHistory.map((game, index) => (
          <li key={index}>
            Round {game.round}: Player chose {game.player}, Computer chose {game.computer} â€” {game.result}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
